class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k){
        unordered_map<long long,long long>check1;
        long long flag;
        long long output=0;
        long long check3=0;

        for(int i=0; i<nums.size(); i++){
            check3=output;
            unordered_map<long long,long long>check2; 
            if(k==nums[i]){
                output++;
                check3=check3/2;
            }
            check2[nums[i]]=1;
            for(auto& [it1,it2]:check1){
                check3++;
                flag=nums[i]&it1;
                if(k==flag){
                    output=output+it2;
                }
                check2[flag]+=it2;
            }
            if(check3==0)check3++;
            check1=check2;
        }
        return output;
    }
};
