class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {    
        int n = nums.size();

        ll cnt = 0;
        unordered_map<ll, ll> mpp;

        for(int i = 0; i < n; ++i) 
        {
            unordered_map<ll, ll> curr;

            if(nums[i] == k) cnt++;

            curr[nums[i]] = 1;

            for(auto& it : mpp) 
            {
                ll x = it.first & nums[i];

                if(x == k) 
                {
                    cnt += it.second;
                }

                curr[x] += it.second;
            }

            mpp = curr;
        }

        return cnt;
    }
};
