class Solution {
public:
    long long countSubarrays(vector<int>& arr, int tgt) {
        int n = arr.size();
        long long count = 0;
        unordered_map<long long, long long> prevResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currResults;

            if (arr[i] == tgt) {
                ++count;
            }

            currResults[arr[i]] = 1;

            for (auto& [val, freq] : prevResults) {
                long long newAndResult = val & arr[i];
                if (newAndResult == tgt) {
                    count += freq;
                }
                currResults[newAndResult] += freq;
            }

            prevResults = currResults;
        }

        return count;
    }
};
