class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll res = 0;

        map<ll, ll> dp;

        for (int i = 0; i < n; ++i) {
            map<ll, ll> curr; 

            if (nums[i] == k) {
                ++res;
            }

            curr[nums[i]] = 1;

            for (auto& [val, freq] : dp) {
                ll temp = val & nums[i];
                if (temp == k) {
                    res += freq;
                }
                curr[temp] += freq;
            }

            dp = curr;
        }

        return res;
    }
};
