class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        map<long long,long long>mp;
        for(int i=0;i<n;i++){
            map<long long,long long>mp1;
            if(nums[i]==k)cnt++;
            mp1[nums[i]]=1;
            for(auto [key,pair]:mp){
                long long bit=key & nums[i];
                if(bit==k)cnt+=pair;
                mp1[bit]+=pair;
            }
            mp=mp1;

        }
        return cnt;

    }
};
