#include <bits/stdc++.h>
using namespace std;

class Solution
{
public:
    long long countSubarrays(vector<int> &nums, int k)
    {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> mep;

        for (int i = 0; i < n; i++)
        {
            unordered_map<long long, long long> mep2;
            if (nums[i] == k)
            {
                count++;
            }
            mep2[nums[i]] = 1;
            for (auto it = mep.begin(); it != mep.end(); ++it)
            {
                long long val = it->first;
                long long f = it->second;
                long long res = val & nums[i];
                if (res == k)
                {
                    count += f;
                }
                mep2[res] += f;
            }
            mep = mep2;
        }

        return count;
    }
};
