class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        c = 0
        old = {}
        for i in range(n):
            new = {}
            if nums[i]==k:
                c+= 1
            new[nums[i]] = 1
            for key,v in old.items():
                addy=key&nums[i]
                if addy==k:
                    c+=v
                new[addy]=new.get(addy, 0)+v
            old = new
        return c
