class Solution { 
public: 
    long long countSubarrays(vector<int>& nums, int k) { 
        int n = nums.size(); 
        long long ans = 0;  
        unordered_map<long long, long long> pR; 
        for (int i = 0; i < n;i++) { 
            unordered_map<long long, long long> cR;  
            if (nums[i] == k) { 
                ans++; 
            } 
            cR[nums[i]] = 1; 

            for (auto& [v, f] : pR) { 
                long long nR = v & nums[i]; 
                if (nR == k) { 
                    ans += f; 
                } 
                cR[nR] += f; 
            }  
            pR = cR; 
        } 
        return ans; 
    } 
};
