class Solution {
public:
    int n;
    long long solve(vector<int>& nums, int k){
         n = nums.size();
        long long ress = 0;
        unordered_map<int, int> b;//RRRRRRR

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> arr;

            if (nums[i] == k) {
                ress += 1;
            }
            arr[nums[i]] = 1;

            for (auto& p : b) {
                int ann = p.first & nums[i];
                int add = p.second;

                if (ann == k) {
                    ress += add;
                }
                arr[ann] += add;
            }

            b = arr;
        }

        return ress;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long my_ans=solve(nums,k);
        return my_ans;
    }
};
