class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long , long long> prev;

        for (int num : nums) {
            unordered_map<long long , long long> curr;

            if (num == k) {
                ans++;
            }
            curr[num]++;

            for (auto& [val, freq] : prev) {
                long long  newa = val & num;
                if (newa == k) {
                    ans += freq;
                }
                curr[newa] += freq;
            }

            prev = curr;
        }

        return ans;
    }
};
