class Solution {
public:

       long long countSubarrays(vector<int>& nums, int k) {

           long long res=0;

            map<long long, long long>m;


        for (long long i = 0; i < nums.size();i++) {
            map<long long, long long> mp;
            mp[nums[i]] = 1;

            if (nums[i] == k) res++;


            for (auto a:m) {
                long long n= a.first&nums[i];
                if (n== k) {
                    res+=a.second;
                }
                mp[n]+=a.second;
            }
            m= mp;
        }

        return res;
    }
};
