class Solution {
public:
    long long countSubarrays(vector<int>&  v, int k) {
        long long int ct = 0;
        map<long long int, long long int> mp;
        for (int i = 0; i < v.size(); i++){
            map<long long int, long long int> mpp;
            if (v[i] == k) ct++;
            mpp[v[i]] = 1;
            for (auto &[it1, it2] : mp) {
               long long temp = it1 &  v[i];
                if (temp == k) ct += it2;
                mpp[temp] += it2;
            }
            mp = mpp;
        }

        return ct;
    }
};
