class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> v(n, vector<int>(32));
        vector<vector<int>> dp(n, vector<int>(32));
        long long ans = 0;
        if(nums[0]==k) ans++;
        for(int i = 0;i<32;i++){
            for(int j = 0;j<n;j++){
                if(j==0){
                    if(nums[j]&(1<<i)) dp[j][i] = -1;
                    else dp[j][i] = 0;
                }
                else{
                    if(nums[j]&(1<<i)) dp[j][i] = dp[j-1][i];
                    else dp[j][i] = j;
                }
            }
        }
        for(int i = 1;i<n;i++){
            int mi = 0;
            int mx = i;
            for(int j = 0;j<32;j++){
                if(k&(1<<j)){
                    mi = max(mi, dp[i][j]+1);
                }
                else mx = min(mx, dp[i][j]);
            }
            if(mx >= mi) ans += 1LL*(mx-mi+1);
        }
        return ans;
    }
};
