class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;

        unordered_map<long long, long long> pr;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 

            if (nums[i] == k) {
                ++res;
            }
            cr[nums[i]] = 1;

            for (auto& [val, freq] : pr) {
                long long n = val & nums[i];
                if (n == k) {
                    res += freq;
                }
                cr[n] += freq;
            }

            pr = cr;
        }

        return res;
    }
};
