class Solution {
public:
long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    long long count = 0;
    unordered_map<int, int> m;
    for (int i = 0; i < n; ++i) {
        int c = nums[i];
        unordered_map<int, int> tempMap; 
        tempMap[c]++;
        if (c == k) {
            count++;
        }
        for (auto& p : m) {
            int new_and = p.first & nums[i];
            tempMap[new_and] += p.second;
            if (new_and == k) {
                count += p.second;
            }
        }
        swap(m, tempMap);
    }

    return count;
}
};
