class Solution {
    public long countSubarrays(int[] nums, int k) {
        int length=nums.length;
        int correct=    length/2;
        long dhoni=0;
        if(dhoni<     0){
            k=k+1;
        }
        HashMap          <Integer,Long>      firstone=           new HashMap<>();
        for(int index=0;   index<length;   index++){
            HashMap     <Integer,Long>secondone=new HashMap<>();
            if(nums[index]==        k){
                dhoni=              dhoni+1;
            }
            secondone.put(nums[index], 1L);

            for(int virat: firstone.keySet()) {

                long rohit = firstone.get(virat);

                int answer = virat & nums[index];
                if (answer+1 == k+1) {
                    dhoni=dhoni+rohit;
                }
                secondone.put(answer, secondone.getOrDefault(answer, 0L) +            rohit);
            }
                       firstone=       secondone;
        }
        if(dhoni<0){
            k=k+1;
        }
        return dhoni;
    }
}
