#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int t) {
        int n = size(nums); ll res = 0, x;
        map<ll, ll> mp, aux;
        for (int i = 0; i < n; ++i) {
            aux.clear(); aux[nums[i]] = 1;
            if (nums[i] == t) ++res;
            for (auto &it : mp) {
                x = (it.first & nums[i]);
                aux[x] += it.second; res += (x != t) ? 0 : it.second;
            }
            mp = aux;
        }
        return res;
    }
};
