class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        def f(p):
            if not p: return 0
            # print([bin(i)[2:] for i in p])
            q = Counter()
            n = len(p)
            ans = 0
            for idx, i in enumerate(p):
                t = Counter()
                for j,v in q.items():
                    tp = i & j
                    if tp == k:
                        ans += (n - idx) * v
                    else:
                        t[tp] += v
                if i == k:
                    ans += n - idx
                else:
                    t[i] += 1
                q = t
            return ans






        p = []
        ans = 0
        for j in nums:
            if j & k == k:
                p.append(j)
            else:
                ans += f(p)
                p = []
        ans += f(p)
        return ans
