class Solution:
    def countSubarrays(self, A, k):
        res, cc = 0, Counter()
        for v in A:
            up_cc = Counter()
            up_cc[v] += 1
            for vv in cc:
                uv = vv & v
                up_cc[uv] += cc[vv]
            res += up_cc[k]
            cc = up_cc
        return res
