class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        s = defaultdict(int)
        ans = 0
        for i in nums:
            t = defaultdict(int)
            for x in s:
                t[x & i] += s[x]
            t[i] += 1
            ans += t[k]
            s = t
        return ans
