class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long>mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mp1; 

            if (nums[i] == k) {
                cnt++;
            }
            mp1[nums[i]] = 1;

            for (auto it : mp) {
                long long temp = it.first& nums[i];
                if (temp == k) {
                    cnt += it.second;
                }
                mp1[temp] += it.second;
            }

            mp = mp1;
        }

        return cnt;
    }
};
