class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        vector<vector<int>> sfx(n,vector<int>(32));
        for(int i =0;i<32;i++) {
             if(nums[n-1]&(1<<i)) sfx[n-1][i] = 1;
            else sfx[n-1][i] = 0;
        }
        if(nums[n-1]==k) ans++;
        for(int i = n-2;i>=0;i--){
            int mini = n-i;;
            int maxi = 1;
            bool flag = true;
            for(int j = 0;j<32;j++){
                if(k&(1<<j)){
                    if((nums[i]&(1<<j))==0) {
                        flag = false;
                    }
                }
            }

            if(!flag) continue;

            for(int j = 0;j<32;j++){
                if((nums[i]&(1<<j))==0) sfx[i][j] = 0;
                else {
                    sfx[i][j] = 1 + sfx[i+1][j];
                    if(k&(1<<j)) mini = min(mini,sfx[i][j]);
                    else maxi = max(maxi,sfx[i][j]+1);
                }
            }

            // cout<<i<<"--"<<mini<<"--"<<maxi<<"--"<<mini-maxi<<"--"<<ans<<endl;
            if(mini>=maxi) ans+= mini-maxi+1;
             // cout<<i<<"--"<<mini<<"--"<<maxi<<"--"<<mini-maxi<<"--"<<ans<<endl;
        }

        return ans;

    }
};
