class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ct=0,ctt=0;
        unordered_map<int,long long>mpp;
        for(int i=0;i<n;i++){
            unordered_map<int,long long>mp;
            if(nums[i]==k) ct++; 
            for(auto it:mpp){
                int bit=nums[i]&it.first;
                mp[bit]+=it.second;
                if(bit==k){
                    ct+=it.second;
                }
                else{
                    ctt++;
                }
            }
            mp[nums[i]]++;
            mpp=mp;
        }
        return ct;
    }
};
