class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n =  nums.size();
        long long res = 0;
        map<long long, long long> m;

        for(int i=0; i<n; i++){
            map<long long, long long> curr;

            if(nums[i] == k) res++;

            curr[nums[i]] = 1;

            for(auto x : m){
                long long check = x.first & nums[i];
                if(check == k){
                    res += x.second;
                }

                curr[check] += x.second;
            }

            m = curr;
        }
        return res;
    }
};
