class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        long long n = nums.size();
        unordered_map<long long, long long> freq;

        for (long long i = 0; i < n; ++i) {
            unordered_map<long long, long long> new_freq;
            if (nums[i] == k) count++;
            new_freq[nums[i]] = 1;

            for (auto& pair : freq) {
                long long new_and = pair.first & nums[i];
                new_freq[new_and] += pair.second;
                if (new_and == k) count += pair.second;
            }

            freq = new_freq;
        }

        return count;
    }
};
