class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        set<int>s;  //val,cnt;
        map<int,long long>freq;
        for(int i=n-1; i>=0; i--){
            set <int> news;
            map <int,long long> nfreq;
            for(auto x:s){
                int val = x & nums[i];
                if(news.find(val)==news.end()){
                    news.insert(val);
                    nfreq[val] = freq[x];
                }
                else{
                    nfreq[val] += freq[x];
                }
            }
            int val = nums[i];
            if(news.find(val)==news.end()){
                news.insert(val);
                nfreq[val] = 1;
            }
            else{
                nfreq[val]++;
            }
            s = news;
            freq = nfreq;
            ans += freq[k];
        }
        return ans;
    }
};
