class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    unordered_map<long long, long long> mp;
    long long result = 0;

    for (long long i = 0; i < n; ++i) {
        unordered_map<long long, long long> cmp;
        for (auto &entry : mp) {
            long long newAnd = entry.first & nums[i];
            cmp[newAnd] += entry.second;
        }
        cmp[nums[i]]++;
        mp = cmp;

        if (mp.find(k) != mp.end()) {
            result += mp[k];
        }
    }
    return result;
    }
};
