class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long total = 0;

        unordered_map<long long, long long> before;

        for (int m = 0; m < size; ++m) {
            unordered_map<long long, long long> now; 

            if (nums[m] == k) {
                ++total;
            }
            now[nums[m]] = 1;

            for (auto& [key, count] : before) {
                long long after = key & nums[m];
                if (after == k) {
                    total += count;
                }
                now[after] += count;
            }

            before = now;
        }

        return total;
    }
};
