#define ll long long
ll max(ll a, ll b){
    if(a > b) return a;
    return b;
}
ll min(ll a, ll b){
    if(a > b) return b;
    return a;
}
class Solution {
    private:
    bool setbit(ll n, ll i) {
        return ((1ll<<i)&n);
    }
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        vector<vector<int>> v(n, vector<int>(33,0));
        for(int j = 0; j < 32; j++) {
            for(int i = n-1; i >= 0; i--) {
                if(setbit(a[i], j)) {
                    if(i == n-1) v[i][j] = 1;
                    else v[i][j] = 1 + v[i + 1][j];
                }
                else {
                    v[i][j] = 0;
                }
            }
        }

        vector<int> pos;
        for(int j = 0; j < 32; j++) {
            if(setbit(k, j)) {
                pos.push_back(j);
            }
        }
        long long ans = 0;
        for(int i = 0; i < n; i++) {


            ll x = n - i;
            for(auto it: pos) {
                x = min(x, v[i][it]);
            }

ll mx = 0;
            for(int j = 0; j < 32; j++) {
                if(find(pos.begin(), pos.end(),j) != pos.end()) continue;
                //rest bits
                mx = max(mx, v[i][j]);
            }
            // cout << x << " " << mx << endl;
            if( (x > mx)) ans += (x - mx);
            // ans += x;
        }
        return ans;
        return 0;
    }
};
