class Solution {
    public long countSubarrays(int[] nums, int k) {
        int[] tk = new int[32];
        int n = nums.length;
        for(int i=0; i<31; i++){
            if(((k>>i)&(1)) == 1){
                tk[i] = -1;
            }
        }
        long ans=0;
        int[] cur = new int[32];
        int l=0, r=0;
        while(r<n){
            update(cur, nums[r], r+1);
            if(checkK(cur, tk)){
                // System.out.println("hi");
                if(valid(cur, tk)){
                    int mn = Integer.MAX_VALUE;
                    for(int i=0; i<32; i++){
                        if(cur[i] != -1){
                            mn = Integer.min(mn, cur[i]);
                        }
                    }
                    ans += mn-l;
                }
            }
            else{
                l=r+1;
                Arrays.fill(cur, 0);
            }
            r++;
        }
        return ans;
    }
    boolean valid(int[] cur, int[] tk){
        for(int i=0; i<32; i++){
            if(cur[i] == -1 && tk[i] != -1){
                return false;
            }
        }
        return true;
    }
    void update(int[] cur, int num, int ind){
        for(int i=0; i<32; i++){
            if((num&(1<<i)) == 0){
                cur[i] = ind;
            }
            else if(cur[i] == 0){
                cur[i] = -1;
            }
        }
    }
    boolean checkK(int[] cur, int[] tk){
        for(int i=0; i<32; i++){
            if(tk[i] == -1 && cur[i] != -1){
                return false;
            }
        }
        return true;
    }
}
