class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        long long totalCount = 0;
        unordered_map<long long, long long> previousAndResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentAndResults;

            if (nums[i] == target) {
                ++totalCount;
            }
            currentAndResults[nums[i]] = 1;

            for (auto& entry : previousAndResults) {
                long long previousAndValue = entry.first;
                long long previousFrequency = entry.second;
                long long newAndValue = previousAndValue & nums[i];

                if (newAndValue == target) {
                    totalCount += previousFrequency;
                }
                currentAndResults[newAndValue] += previousFrequency;
            }

            previousAndResults = currentAndResults;
        }
        return totalCount;
    }
};
