class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        vector<int> cur(31 , 0);

        long long int ans = 0;

        int left = 0 , n = nums.size(), val = INT_MAX;

        for(int i = 0 ; i < n ; i++){
            for(int j = 0 ; j < 31 ; j++){
                if(( (1 << j) & nums[i] ) == 0){
                    cur[j] ++;

                    if(cur[j] == 1){
                        val -= (1 << j);
                    }
                }
            }

            while(val < k && left <= i){
                for(int j = 0 ; j < 31 ; j++){
                    if(( (1 << j) & nums[left] ) == 0){
                        cur[j] --;

                        if(cur[j] == 0){
                            val += (1 << j);
                        }
                    }
                }
                left++;
            }

            ans += i - left + 1;
            // cout<<i<<' '<<left<<' '<<val<<endl;
        }
        // cout<<ans<<endl;

        left = 0 , val = INT_MAX;
        cur = vector<int>(31 , 0);


        for(int i = 0 ; i < n ; i++){
            for(int j = 0 ; j < 31 ; j++){
                if(( (1 << j) & nums[i] ) == 0){
                    cur[j] ++;

                    if(cur[j] == 1){
                        val -= (1 << j);
                    }
                }
            }

            while(val <= k && left <= i){
                for(int j = 0 ; j < 31 ; j++){
                    if(( (1 << j) & nums[left] ) == 0){
                        cur[j] --;

                        if(cur[j] == 0){
                            val += (1 << j);
                        }
                    }
                }
                left++;
            }

            ans -= i - left + 1;
            // cout<<i<<' '<<left<<' '<<val<<endl;
        }

        return ans;
    }
};
