class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> cnt;
        long long res=0;
        for(int i=0;i<n;i++){
            map<int,int> temp;
            temp[nums[i]]+=1;
            for(auto [j,v]:cnt){
                temp[(j&nums[i])]+=v;
            }
            cnt=temp;
            res+=cnt[k];
        }
        return res;
    }
};
