class Solution {
public:
#define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> count;

        for (long long i = 0; i < n; ++i) {
            unordered_map<long long, long long> Freq;
            if (nums[i] == k) {
                cnt++;
            }
            Freq[nums[i]]++;

            for (auto it : count) {
                ll newValue = it.first & nums[i];
                if (newValue == k) {
                    cnt += it.second;
                }
                Freq[newValue] += it.second;
            }
            count = Freq;
        }
        return cnt;
    }
};
