class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();


        int sum=nums[0];
         map<long long int,long long int>m;
   long long int ans=0;
        for(int i=0;i<n;i++){
        map<long long int,long long int>m1;
            for(auto val:m){
                m1[val.first]=val.second;
            }
            m.clear();
            for(auto val:m1){
            long long int x=val.first,y=val.second;
                x=x&nums[i];
                if(x==k)ans+=y;
                m[x]+=y;

            }
            if(nums[i]==k)ans++;
    m[nums[i]]++;
            // cout<<ans<<endl;

        }
        return ans;

    }
};
