class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        res = 0
        m = defaultdict(int)
        for num in nums:
            m2 = defaultdict(int)
            for p, f in m.items():
                m2[p&num] += f
            m2[num] += 1

            m = m2
            res += m[k]
        return res
