class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cntuu = 0;

        unordered_map<long long, long long> Myrc;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> Yrc; 

            if (nums[i] == k) {
                cntuu++;
            }
            Yrc[nums[i]] = 1;

            for (auto& [valuu, freqii] : Myrc) {

                long long newans = valuu & nums[i];
                if (newans == k) {
                    cntuu += freqii;
                }
                Yrc[newans] += freqii;
            }

            Myrc = Yrc;
        }

        return cntuu;
    }
};
