class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans=0;
        int n=a.size();
        int f=0;
        int left=0;
        vector <int> pos(31);
        vector <int> v;
        long long x=1ll;
        vector <bool> vis(31);
        for (int j=0;j<31;j++,x=x*2ll){
            if (k&x){

            }
            else v.push_back(j),vis[j]=true;
        }
        for (int i=0;i<n;i++){
            if (i==left) f=a[i];
            else f=f&a[i];
            x=1ll;
            bool ok=true;
            for (int j=0;j<31;j++,x=x*2ll){
                if (a[i]&x){

                }
                else{
                    if (!vis[j]) ok=false;
                    pos[j]=i;
                }
            }
            if (f==k){
                int mn=i;
                for (auto y:v){
                    mn=min(mn,pos[y]);
                }
               // cout<<i<<" "<<left<<" "<<mn<<"\n";
                ans+=mn-left;
                ans++;
            }
            else if(!ok){
                left=i+1;
            }
        }
        return ans;
    }
};
