class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> prev;
        for(int i=0; i<n; i++){
            unordered_map<long long, long long> curr;

            if(nums[i] == k)count++;
            curr[nums[i]]++;
            for(auto &it : prev){
                long long value = it.first;
                long long freq = it.second;

                long long newVal = value & nums[i];
                if(newVal == k)count += freq;
                curr[newVal] += freq;
            }
            prev = curr;
        } 
        return count;
    }
};
