class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        vector<array<int,32>> v(n);

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < 32; ++j)
                v[i][j] = i? v[i-1][j] : -1;

            for (int j = 0; j < 32; ++j) {
                if (!(nums[i]>>j&1))
                    v[i][j] = i;
            }
        }

        //cout << v[0][0] << endl;
        for (int i = 0; i < n; ++i) {
            bool ok = true;
            for (int j = 0; j < 32; ++j) {
                if ((k>>j&1) && !(nums[i]>>j&1)) {
                    ok = false;
                    break;
                }
            }

            if (!ok)
                continue;

            int l = i, r = -1;
            for (int j = 0; j < 32; ++j) {
                if (!(k>>j&1))
                    l = min(l, v[i][j]);
                else
                    r = max(r, v[i][j]+1);
            }

            if (k == 0)
                r = 0;

            //cout << i << " " << r << " " << l << endl;
            if (r <= l)
                ans += l-r+1;
        }

        return ans;
    }
};
