class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        unordered_map<long long,long long> mp;
        for(int i=0;i<nums.size();i++)
        {
            unordered_map<long long,long long> mp2;
            if(nums[i]==k)
            {
                ans++;
            }
            mp2[nums[i]]=1;
            for(auto& [val,j]:mp)
            {
                long long temp=val&nums[i];
                if(temp==k)
                {
                    ans+=j;
                }
                mp2[temp]+=j;
            }
            mp=mp2;
        }
        return ans;
    }
};
