class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        N=len(nums)
        SZ=32
        bitmap=[0]*SZ
        ans=0
        for idx,a in enumerate(nums):
            num=a
            one=idx+1
            zero=0
            for i in range(SZ):
                if (num>>i)&1==1:
                    bitmap[i]+=1
                else:
                    bitmap[i]=0
                if (k>>i)&1==1:
                    one=min(one, bitmap[i])
                else:
                    zero=max(zero, bitmap[i])
            # print(one, zero, bitmap)
            if one>zero:
                # print("size:", one-zero)
                ans+=one-zero
        return ans
