class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int, int> data;
        int n = nums.size();
        long long ret = 0;

        int i = 0;
        while (i < n)
        {
            if (nums[i] == k)
            {
                ret++;
            }
            map<int, int> newData;
            if(nums[i]>=k)
            {
                newData[nums[i]] = 1;
            }
            for (auto [num, count] : data)
            {
                int newnum = (num & nums[i]);
                if (newnum == k)
                {
                    ret += count;
                }
                if (newnum >= k)
                {
                    newData[newnum] += count;
                }
            }
            data = newData;
            i++;
        }
        return ret;
    }
};
