class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        cnt = Counter()
        for i, x in enumerate(nums):
            st = Counter()
            cnt[x] += 1
            for k2, v in cnt.items():
                st[k2 & x] += v
            cnt = st
            ans += cnt[k]



        return ans
