class Solution {
    public long countSubarrays(int[] nums, int k) {
        long res = 0;
        var hm = new HashMap<Integer, Long>();
        for(int i : nums){
            var next = new HashMap<Integer, Long>();
            for(var e : hm.entrySet()){
                next.merge(e.getKey() & i, e.getValue(), Long::sum);
            }
            next.merge(i, 1l, Long::sum);
            res += next.getOrDefault(k, 0l);
            hm = next;
        }
        return res;
    }
}
