class Solution:
    def countSubarrays(self, nums: List[int], z: int) -> int:
        n = len(nums)
        ans = 0
        a = []
        for i, num in enumerate(nums):
            a.append([num, i])
            k = 0
            for p in a:
                p[0] &= num
                if a[k][0] == p[0]:
                    continue
                else:
                    k += 1
                    a[k] = p
            del a[k + 1:]
            # 本题只用到了 ors[0]，如果题目改成任意给定数值，可以在 ors 中查找
            for j, p in enumerate(a):
                if p[0] < z:
                    continue
                elif p[0] == z:
                    if j == len(a) - 1:
                        ans += i - p[1] + 1
                    else:
                        ans += a[j + 1][1] - p[1]
                else:
                    break
        return ans
