class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums) 
        count = 0

        prev = {}

        for i in range(n):
            curr = {}

            if nums[i] == k:
                count += 1

            curr[nums[i]] = 1

            for val, freq in prev.items():
                currAND = val & nums[i]
                if currAND == k:
                    count += freq
                if currAND in curr:
                    curr[currAND] += freq
                else:
                    curr[currAND] = freq

            prev = curr

        return count
