class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> mpp1;

        for (int i = 0; i < n; i++)
        {
            unordered_map<long long, long long> mpp2; 

            if (nums[i] == k) 
            {
                ans++;
            }
            mpp2[nums[i]] = 1;

            for (auto& [val, freq] : mpp1) {
                long long newResult = val & nums[i];
                if (newResult == k) {
                    ans+= freq;
                }
                mpp2[newResult] += freq;
            }

            mpp1 = mpp2;
        }

        return ans;
    }
};
