class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int a = nums.size();

        long long b = 0;

        unordered_map<long long, long long> c;

        int d = 0;
        while (d < a) {
            unordered_map<long long, long long> e;

            if (nums[d] == k) {
                ++b;
            }

            e[nums[d]] = 1;

            auto it = c.begin();
            while (it != c.end()) {
                long long f = it->first;
                long long g = it->second;

                long long h = f & nums[d];

                if (h == k) {
                    b += g;
                }

                e[h] += g;

                ++it;
            }

            c = e;
            ++d;
        }

        return b;
    }
};
