class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size() ;

        map<int,int> store ;
        long long count = 0 ;

        for(int i = 0 ; i < n ; i++ ){
            map<int,int> new_store ;
            int curr_and = nums[i] ;
            new_store[curr_and] += 1 ;
            if(curr_and == k ) count++ ;

            for( auto &[prev_and, freq] : store){
                int new_and = curr_and&prev_and ;
                if(new_and == k) count += freq ;
                new_store[new_and] += freq ;
            }

            store = new_store ;
        }
        return count ;

    }
};
